CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_idade_diagnostico (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w varchar(255);

BEGIN
    begin
      select  obter_idade(obter_dados_pf(cd_pessoa_fisica_p, 'DN'), min(coalesce(a.dt_inicio, coalesce(a.dt_cid, a.dt_diagnostico))), 'A') idade
      into STRICT    ds_retorno_w
      from    diagnostico_doenca a
      where   a.nr_atendimento in (
                                    SELECT  b.nr_atendimento
                                    from    atendimento_paciente b
                                    where   b.cd_pessoa_fisica = cd_pessoa_fisica_p
                                  )
      and     alto_custo_pck.is_alto_custo(a.cd_doenca, current_setting('alto_custo_hemofilia_pck.ie_tipo_doenca_w')::regra_alto_custo.ie_tipo_doenca%type) = 'S';
    exception
      when no_data_found then
        ds_retorno_w := '9998'; --9998= dato no disponible
    end;

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
