CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_total_atend_especidalidade (ie_tipo_evolucao_p evolucao_paciente.ie_tipo_evolucao%type) RETURNS bigint AS $body$
DECLARE

    ds_retorno_w bigint;

BEGIN
    select  count(e.nr_atendimento)
    into STRICT    ds_retorno_w
    from    table(alto_custo_pck.get_atendimentos_paciente(current_setting('alto_custo_hemofilia_pck.option_all_w')::varchar(1))) t,
            atendimento_paciente a,
            evolucao_paciente e,
            usuario u
    where   a.nr_atendimento = t.nr_atendimento
    and     e.nr_atendimento = a.nr_atendimento
    and     a.ie_tipo_atendimento in (6, 8, 12, 14, 21)
    and     u.cd_pessoa_fisica = e.cd_medico
    and     u.ie_tipo_evolucao = ie_tipo_evolucao_p;

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
