CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_total_tipo_hemorragia (ie_hemorragia_p diagnostico_classif_adic.ie_hemorragia%type) RETURNS bigint AS $body$
DECLARE

    ds_retorno_w bigint;

BEGIN
    select  count(d.nr_atendimento)
    into STRICT    ds_retorno_w
    from    table(alto_custo_pck.get_atendimentos_paciente(current_setting('alto_custo_hemofilia_pck.option_all_w')::varchar(1))) t,
            diagnostico_doenca d,
            diagnostico_classif_adic dc,
            cid_doenca c
    where   d.nr_atendimento = t.nr_atendimento
    and     dc.cd_doenca = d.cd_doenca
    and     c.cd_doenca = dc.cd_doenca
    and     c.ie_doenca_cronica_mx = 'HEMOR'
    and     dc.ie_hemorragia = ie_hemorragia_p;

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
