CREATE OR REPLACE FUNCTION alto_custo_hepatite_pck.get_dt_tratamento ( cd_pessoa_fisica_p text, ie_tipo_doenca_p regra_alto_custo.ie_tipo_doenca%type, ie_data_p text) RETURNS timestamp AS $body$
DECLARE

    ds_retorno_w           varchar(10);
    dt_inicio_tratamento_w timestamp;
    dt_fim_tratamento_w    timestamp;

BEGIN
    BEGIN
      SELECT dt_inicio_tratamento,
        dt_fim_tratamento
      INTO STRICT dt_inicio_tratamento_w,
        dt_fim_tratamento_w
      FROM (SELECT a.dt_inicio_tratamento,
          a.dt_fim_tratamento
        FROM historico_saude_tratamento a,
          protocolo b
        WHERE a.cd_pessoa_fisica                                        = cd_pessoa_fisica_p
        AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
        AND b.cd_protocolo                                              = a.cd_protocolo
        AND alto_custo_pck.is_alto_custo(b.cd_doenca, ie_tipo_doenca_p) = 'S'
        ORDER BY a.dt_registro DESC
        ) alias2 LIMIT 1;
    EXCEPTION
      WHEN OTHERS THEN
        ds_retorno_w := '';
    END;

    CASE ie_data_p
    WHEN 'I' THEN
      ds_retorno_w := dt_inicio_tratamento_w;
    WHEN 'F' THEN
      ds_retorno_w := dt_fim_tratamento_w;
    ELSE
      ds_retorno_w := '';
    END CASE;

    RETURN ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
