CREATE OR REPLACE FUNCTION alto_custo_hiv_pck.get_patologias (cd_pessoa_fisica_p text, ie_doenca_cronica_p text, ie_presente_p text default 'N') RETURNS varchar AS $body$
DECLARE


	  ds_retorno_w	varchar(1);

	
BEGIN
    select max(qtd)
    into STRICT ds_retorno_w 
    from
    (SELECT CASE WHEN count(1)=0 THEN  0  ELSE 1 END  qtd
    from paciente_antec_clinico a,
      cid_doenca b
    where b.cd_doenca_cid      = a.cd_doenca
    and a.cd_pessoa_fisica     = cd_pessoa_fisica_p
    and b.ie_doenca_cronica_mx = ie_doenca_cronica_p
    and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    and coalesce(a.dt_inativacao::text, '') = ''
    and (a.dt_inicio IS NOT NULL AND a.dt_inicio::text <> '')
    and ((ie_presente_p = 'S' and coalesce(a.dt_fim::text, '') = '') or (ie_presente_p = 'N' and (a.dt_fim IS NOT NULL AND a.dt_fim::text <> '')))
    
union

    SELECT CASE WHEN count(1)=0 THEN  0  ELSE 1 END  qtd
    from diagnostico_doenca a,
      cid_doenca b,
      atendimento_paciente c
    where b.cd_doenca_cid      = a.cd_doenca
    and c.nr_atendimento       = a.nr_atendimento
    and c.cd_pessoa_fisica     = cd_pessoa_fisica_p
    and b.ie_doenca_cronica_mx = ie_doenca_cronica_p
    and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    and coalesce(a.dt_inativacao::text, '') = ''
    and (a.dt_inicio IS NOT NULL AND a.dt_inicio::text <> '')
    and ((ie_presente_p = 'S' and coalesce(a.dt_fim::text, '') = '') or (ie_presente_p = 'N' and (a.dt_fim IS NOT NULL AND a.dt_fim::text <> '')))
    ) alias19;

		return ds_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
