SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_hiv_pck.get_pessoa_tuberculose ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w  varchar(255);

	
BEGIN

		begin

		select	case
				when is_ini_between_dt_corte = '1' then '1'
				when is_ini_between_months = '1' and is_fim_between_dt_corte = '1' then '2'
				when is_ini_between_months = '2' then '3'
				else '0' end ie_pessoa_tuberculose
		into STRICT	ds_retorno_w
		from (
		SELECT  case
				  when dt_inicio between alto_custo_pck.get_data_inicio_emissao_guia and alto_custo_pck.get_data_corte and coalesce(dt_fim::text, '') = '' then '1'
				  else '0' end is_ini_between_dt_corte,
				case
				  when dt_fim between alto_custo_pck.get_data_inicio_emissao_guia and alto_custo_pck.get_data_corte then '1'
				  else '0' end is_fim_between_dt_corte,
				case 
				  when trunc(dt_inicio) between add_months(trunc(clock_timestamp()),-6) and trunc(clock_timestamp()) then '1'
				  when trunc(dt_inicio) < add_months(trunc(clock_timestamp()),-6) then '2'
				  else '0' end is_ini_between_months,
				dt_inicio
		from (
				SELECT  max(dd.dt_diagnostico) dt_inicio, dd.dt_fim dt_fim
				from    diagnostico_doenca dd
				where   (dd.dt_liberacao IS NOT NULL AND dd.dt_liberacao::text <> '')
				and     coalesce(dd.dt_inativacao::text, '') = ''
				and     (dd.dt_diagnostico IS NOT NULL AND dd.dt_diagnostico::text <> '')
				and     dd.nr_atendimento in (select nr_atendimento from atendimento_paciente where cd_pessoa_fisica = cd_pessoa_fisica_p)
				and     dd.cd_doenca in ( select cd_doenca_cid from cid_doenca where ie_doenca_cronica_mx in ('TUBER', 'TEXPU'))
				group by dd.dt_fim
				
union

				select  max(pac.dt_inicio) dt_inicio, pac.dt_fim dt_fim
				from    paciente_antec_clinico pac
				where   (pac.dt_liberacao IS NOT NULL AND pac.dt_liberacao::text <> '')
				and     coalesce(pac.dt_inativacao::text, '') = ''
				and     (pac.dt_inicio IS NOT NULL AND pac.dt_inicio::text <> '')
				and     pac.cd_pessoa_fisica = cd_pessoa_fisica_p
				and     pac.cd_doenca in ( select cd_doenca_cid from cid_doenca where ie_doenca_cronica_mx in ('TUBER', 'TEXPU'))
				group by pac.dt_fim) a
		order by dt_inicio desc) alias25 LIMIT 1;
		
		exception
		  when no_data_found then
			ds_retorno_w := 0;
		end;

		return ds_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
