CREATE OR REPLACE PROCEDURE alto_custo_pck.gerar_pacientes (dt_inicio_p timestamp, dt_fim_p timestamp, ie_tipo_doenca_p text, nr_seq_alto_custo_p bigint, nm_usuario_p text) AS $body$
DECLARE

    cd_pessoa_fisica_w      pessoa_fisica.cd_pessoa_fisica%type;
    nr_atendimento_w        atendimento_paciente.nr_atendimento%type;

    c_pacientes CURSOR FOR
      SELECT  distinct
              a.cd_pessoa_fisica,
              max(a.nr_atendimento) nr_atendimento
      from    atendimento_paciente a,
              diagnostico_doenca d,
              atend_categoria_convenio c
      where   d.nr_atendimento = a.nr_atendimento
      and     d.ie_situacao = 'A'
      and     coalesce(a.dt_cancelamento::text, '') = ''
      and     (d.dt_liberacao IS NOT NULL AND d.dt_liberacao::text <> '')
      and     alto_custo_pck.is_alto_custo(d.cd_doenca, ie_tipo_doenca_p) = 'S'
      and     trunc(a.dt_entrada) >= trunc(dt_inicio_p)
      and     trunc(a.dt_entrada) <= trunc(dt_fim_p)
      and     c.nr_atendimento = a.nr_atendimento
      and     c.cd_convenio = (
                                SELECT  ac.cd_convenio
                                from    alto_custo ac
                                where   ac.nr_sequencia = nr_seq_alto_custo_p
                              )
      group by  a.cd_pessoa_fisica;


BEGIN
    PERFORM set_config('alto_custo_pck.data_ini_emissao_guia_w', trunc(dt_inicio_p), false);
    PERFORM set_config('alto_custo_pck.data_corte_w', trunc(dt_fim_p), false);
    PERFORM set_config('alto_custo_pck.nr_seq_alto_custo_w', nr_seq_alto_custo_p, false);

    update  atend_anzics
    set     dt_inativacao = clock_timestamp()
    where   nr_seq_alto_custo = current_setting('alto_custo_pck.nr_seq_alto_custo_w')::alto_custo.nr_sequencia%type;

    for c_pacientes_w in c_pacientes
    loop
      begin
        nr_atendimento_w := c_pacientes_w.nr_atendimento;
        CALL generate_atend_anzics(nr_atendimento_w, null, nm_usuario_p, null, null, nr_seq_alto_custo_p);
      end;
    end loop;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
