CREATE OR REPLACE FUNCTION alto_custo_pck.get_info_afiliacao ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_data_p text ) RETURNS timestamp AS $body$
DECLARE

    dt_retorno_w timestamp;

BEGIN
    select  case ie_data_p
              when 'A' then
                max(a.dt_afiliacao)
              when 'D' then
                max(a.dt_fim_vigencia)
              else null
            end
    into STRICT    dt_retorno_w
    from    pessoa_titular_convenio a,
            atend_categoria_convenio b
    where   a.cd_pessoa_fisica = cd_pessoa_fisica_p
    and     b.cd_convenio = a.cd_convenio
    and     a.cd_categoria = b.cd_categoria
    and     b.nr_atendimento = nr_atendimento_p;

    return dt_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
