CREATE OR REPLACE FUNCTION alto_custo_pck.get_regime_afiliacao (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w      varchar(255);

BEGIN
    select  CASE WHEN               max(c.cd_tipo_aseguramiento)=1 THEN  'C' WHEN               max(c.cd_tipo_aseguramiento)=2 THEN  'S' WHEN               max(c.cd_tipo_aseguramiento)=4 THEN  'E'  ELSE 'N' END 
    into STRICT    ds_retorno_w
    from    atend_categoria_convenio a,
            convenio b,
            convenio_plano c 
    where   a.nr_atendimento = nr_atendimento_p
    and     b.cd_convenio = a.cd_convenio
    and     c.cd_convenio = b.cd_convenio;

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
