CREATE OR REPLACE FUNCTION alto_custo_pck.get_telefone_pessoa (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w      varchar(255);


BEGIN
    begin
      select  coalesce(trim(both '-' from coalesce(max(pf.nr_telefone_celular), max(cpf.nr_telefone)) 
                                    || '-' || max(cpf.ds_fone_adic)),0)
      into STRICT    ds_retorno_w
      FROM pessoa_fisica pf
LEFT OUTER JOIN compl_pessoa_fisica cpf ON (pf.cd_pessoa_fisica = cpf.cd_pessoa_fisica)
WHERE pf.cd_pessoa_fisica = cd_pessoa_fisica_p;
    exception
      when others then
        ds_retorno_w := '';
    end;

    return elimina_caracteres_especiais(ds_retorno_w);
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
