CREATE OR REPLACE FUNCTION alto_custo_pck.is_alto_custo (cd_doenca_p cid_doenca.cd_categoria_cid%type, ie_tipo_doenca_p regra_alto_custo.ie_tipo_doenca%type) RETURNS varchar AS $body$
DECLARE

    is_alto_custo_w varchar(1);

BEGIN
    select  coalesce(max(is_alto_custo), 'N')
    into STRICT    is_alto_custo_w
    from (
      SELECT  'S' is_alto_custo
      from    regra_alto_custo a,
              regra_alto_custo_cid b
      where   a.nr_sequencia = b.nr_seq_regra_alto_custo
      and (ie_tipo_doenca_p = 'S' or a.ie_tipo_doenca = ie_tipo_doenca_p)
      and     b.cd_categoria_cid = elimina_caracteres_especiais(cd_doenca_p)
      
union all

      SELECT  'S' is_alto_custo
      from    cid_doenca c
      where   c.cd_doenca_cid = elimina_caracteres_especiais(cd_doenca_p)
      and     c.cd_categoria_cid in (
                                        select  b.cd_categoria_cid 
                                        from    regra_alto_custo a,
                                                regra_alto_custo_cid b
                                        where   a.nr_sequencia = b.nr_seq_regra_alto_custo
                                        and (ie_tipo_doenca_p = 'S' or a.ie_tipo_doenca = ie_tipo_doenca_p)
                                      )
    ) t;

    return is_alto_custo_w;
  END;

$body$
