CREATE OR REPLACE FUNCTION alto_custo_pck.obter_se_tipo_doenca ( cd_doenca_p text, ie_tipo_doenca_p text ) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w      varchar(255);

BEGIN
    select  CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END 
    into STRICT    ds_retorno_w
    from    cid_doenca cid
    where   cid.cd_doenca = cd_doenca_p
    and     cid.cd_categoria_cid in ( 
                                      SELECT  racc.cd_categoria_cid
                                      from    regra_alto_custo_cid racc
                                      where   racc.nr_seq_regra_alto_custo = (
                                                                                SELECT  rac.nr_sequencia
                                                                                from    regra_alto_custo rac
                                                                                where   rac.ie_tipo_doenca = ie_tipo_doenca_p
                                                                              )
                                    );

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
