CREATE OR REPLACE FUNCTION alto_custo_simplificado_pck.get_dt_diag_tuberculose (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


	ds_retorno_w timestamp;

	
BEGIN

		select  min(dd.dt_cid)
		into STRICT    ds_retorno_w
		from    diagnostico_doenca dd
		where   (dd.dt_liberacao IS NOT NULL AND dd.dt_liberacao::text <> '')
		and     coalesce(dd.dt_inativacao::text, '') = ''
		and     dd.nr_atendimento = nr_atendimento_p
		and     dd.cd_doenca in (	SELECT  cd_doenca_cid
									from    cid_doenca 
									where   ie_doenca_cronica_mx in ('TUBER', 'TEXPU'));

		return ds_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
