CREATE OR REPLACE FUNCTION alto_custo_simplificado_pck.get_if_tuberculose (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


	ds_retorno_w bigint;

	
BEGIN

		select CASE WHEN max(cd_doenca)=0 THEN  0  ELSE 1 END
		into STRICT ds_retorno_w
		from (
		  SELECT  count(pac.cd_doenca) cd_doenca
		  from    paciente_antec_clinico pac
		  where   (pac.dt_inicio IS NOT NULL AND pac.dt_inicio::text <> '')
		  and     (pac.dt_liberacao IS NOT NULL AND pac.dt_liberacao::text <> '')
		  and     coalesce(pac.dt_inativacao::text, '') = ''
		  and     pac.cd_pessoa_fisica = cd_pessoa_fisica_p
		  and     pac.cd_doenca in (	SELECT cd_doenca_cid 
										from cid_doenca 
										where ie_doenca_cronica_mx in ('TUBER', 'TEXPU'))
		  
union

		  select  count(dd.cd_doenca) cd_doenca
		  from    diagnostico_doenca dd
		  where   (dd.dt_liberacao IS NOT NULL AND dd.dt_liberacao::text <> '')
		  and     coalesce(dd.dt_inativacao::text, '') = ''
		  and     dd.nr_atendimento in (select nr_atendimento from atendimento_paciente where cd_pessoa_fisica = cd_pessoa_fisica_p)
		  and     dd.cd_doenca in (	select cd_doenca_cid 
									from cid_doenca 
									where ie_doenca_cronica_mx in ('TUBER', 'TEXPU'))) alias13;

		return ds_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
