CREATE OR REPLACE FUNCTION anzics_pck.get_inotropes_vasopressor_ind ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_atepacu_p bigint) RETURNS bigint AS $body$
DECLARE

    nr_reterno_w    bigint     := 0;
    ie_prescribed_w varchar(1) := 'S';

BEGIN
    CALL anzics_pck.set_icu_date(nr_seq_atepacu_p);
    SELECT CASE WHEN COUNT(1)=0 THEN 'N'  ELSE 'S' END
    INTO STRICT ie_prescribed_w
    FROM prescr_medica a ,
      prescr_material b ,
      material c
    WHERE a.nr_prescricao  = b.nr_prescricao
    AND b.cd_material      = c.cd_material
    AND coalesce(a.dt_suspensao::text, '') = ''
    AND coalesce(b.dt_suspensao::text, '') = ''
    AND a.dt_prescricao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND a.nr_atendimento = nr_atendimento_p
    AND EXISTS (SELECT 1
      FROM anzics_medicines r
      WHERE r.cd_material      = b.cd_material
      AND r.ie_inotropes_vasop ='S'
      AND r.ie_situacao        ='A'
      );
    IF ( ie_prescribed_w = 'S') THEN
      nr_reterno_w      := 1;
    END IF;
    RETURN nr_reterno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
