CREATE OR REPLACE FUNCTION anzics_pck.get_intubation_status ( nr_atendimento_p bigint, dt_start_date_p timestamp, dt_end_date_p timestamp) RETURNS varchar AS $body$
DECLARE

    ie_intubated_w varchar(1) := 'N';

BEGIN
    SELECT CASE WHEN COUNT(1)=0 THEN 'N'  ELSE 'S' END 
    INTO STRICT ie_intubated_w
    FROM atendimento_monit_resp a
    WHERE a.nr_atendimento = nr_atendimento_p
    AND a.dt_monitorizacao BETWEEN dt_start_date_p AND dt_end_date_p
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    AND (a.ie_canula_traqueo IS NOT NULL AND a.ie_canula_traqueo::text <> '');
    RETURN ie_intubated_w;
  END;

