SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION anzics_pck.get_resp_rate_vent_status ( nr_atendimento_p bigint, nr_seq_atepacu_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

    nr_min_rr_vent_w smallint;
    nr_max_rr_vent_w smallint;

BEGIN
    CALL anzics_pck.set_icu_date(nr_seq_atepacu_p);
    SELECT CASE WHEN MAX(a.ie_respiracao)='ESPONT' THEN 1  ELSE 0 END
    INTO STRICT nr_max_rr_vent_w
    FROM atendimento_monit_resp a
    WHERE a.nr_atendimento = nr_atendimento_p
    AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    AND qt_freq_resp     =
      (SELECT MAX(a.qt_freq_resp)
      FROM atendimento_monit_resp a
      WHERE a.nr_atendimento = nr_atendimento_p
      AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
      AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
      AND coalesce(a.dt_inativacao::text, '') = ''
      );
    SELECT CASE WHEN MAX(a.ie_respiracao)='ESPONT' THEN 1  ELSE 0 END
    INTO STRICT nr_min_rr_vent_w
    FROM atendimento_monit_resp a
    WHERE a.nr_atendimento = nr_atendimento_p
    AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    AND qt_freq_resp     =
      (SELECT MIN(a.qt_freq_resp)
      FROM atendimento_monit_resp a
      WHERE a.nr_atendimento = nr_atendimento_p
      AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
      AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
      AND coalesce(a.dt_inativacao::text, '') = ''
      );
    IF (ie_opcao_p = 'RRHI_VENT') THEN
      RETURN nr_max_rr_vent_w;
    elsif (ie_opcao_p = 'RRLO_VENT') THEN
      RETURN nr_min_rr_vent_w;
    END IF;
    RETURN NULL;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
