SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION anzics_pck.get_tracheostomy_indicator ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_atepacu_p bigint) RETURNS bigint AS $body$
DECLARE

    nr_reterno_w bigint := 0;
    qt_reg_w     bigint := 0;

BEGIN
    CALL anzics_pck.set_icu_date(nr_seq_atepacu_p);
    SELECT COUNT(*)
    INTO STRICT qt_reg_w
    FROM atendimento_monit_resp a
    WHERE a.nr_atendimento = nr_atendimento_p
    AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    AND a.ie_canula_traqueo IN ('TM','TP');
    IF ( qt_reg_w           >= 1) THEN
      nr_reterno_w          := 1;
    END IF;
    RETURN nr_reterno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
