SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION anzics_pck.get_urine24h ( nr_atendimento_p bigint, nr_seq_atepacu_p bigint, nr_seq_tipo_p bigint) RETURNS bigint AS $body$
DECLARE

    qt_total_urina_w   double precision;
    nr_urine_records_w bigint;
    ds_tipo_output_urine_w varchar(255);

BEGIN
    CALL anzics_pck.set_icu_date(nr_seq_atepacu_p);
    Obter_Param_Usuario(281,1575,obter_perfil_ativo,wheb_usuario_pck.get_nm_usuario,wheb_usuario_pck.get_cd_estabelecimento,ds_tipo_output_urine_w);
    SELECT SUM(c.qt_volume)
    INTO STRICT qt_total_urina_w
    FROM atendimento_perda_ganho c
    WHERE c.nr_atendimento = nr_atendimento_p
    AND ((c.nr_seq_tipo      = nr_seq_tipo_p) or ((ds_tipo_output_urine_w IS NOT NULL AND ds_tipo_output_urine_w::text <> '') and (OBTER_SE_CONTIDO_CHAR_SEPAR(c.nr_seq_tipo,ds_tipo_output_urine_w,'-')='S')))
    AND c.dt_medida BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
    AND coalesce(c.dt_inativacao::text, '') = '';
    SELECT COUNT(*)
    INTO STRICT nr_urine_records_w
    FROM atendimento_perda_ganho c
    WHERE c.nr_atendimento = nr_atendimento_p
    AND ((c.nr_seq_tipo      = nr_seq_tipo_p) or ((ds_tipo_output_urine_w IS NOT NULL AND ds_tipo_output_urine_w::text <> '') and (OBTER_SE_CONTIDO_CHAR_SEPAR(c.nr_seq_tipo,ds_tipo_output_urine_w,'-')='S')))
    AND c.dt_medida BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
    AND (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
    AND coalesce(c.dt_inativacao::text, '') = '';
    IF (nr_urine_records_w > 0) THEN
      qt_total_urina_w   := ((qt_total_urina_w/nr_urine_records_w)*24);
    ELSE
      qt_total_urina_w := 0;
    END IF;
    RETURN qt_total_urina_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
