SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION anzics_pck.get_vent_indicator ( nr_atendimento_p bigint, nr_seq_atepacu_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

    nr_inv_vent_status_w  smallint;
    nr_ninv_vent_status_w smallint;
    qt_reg_w              smallint;

BEGIN
    CALL anzics_pck.set_icu_date(nr_seq_atepacu_p);
    IF (ie_opcao_p = 'INV_IND') THEN
      SELECT COUNT(1)
      INTO STRICT qt_reg_w
      FROM atendimento_monit_resp a
      WHERE a.nr_atendimento = nr_atendimento_p
      AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
      AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
      AND a.ie_respiracao IN ('VMIFB','VMI')
      AND coalesce(a.dt_inativacao::text, '') = '';
      IF (qt_reg_w        >= 1) THEN
        RETURN 1;
      END IF;
    elsif (ie_opcao_p = 'NIV_IND') THEN
      SELECT COUNT(1)
      INTO STRICT qt_reg_w
      FROM atendimento_monit_resp a
      WHERE a.nr_atendimento = nr_atendimento_p
      AND a.dt_monitorizacao BETWEEN current_setting('anzics_pck.dt_inicio_w')::timestamp AND current_setting('anzics_pck.dt_fim_w')::timestamp
      AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
      AND a.ie_respiracao IN ('VMNI','BORB')
      AND coalesce(a.dt_inativacao::text, '') = '';
      IF (qt_reg_w        >= 1) THEN
        RETURN 1;
      END IF;
    END IF;
    RETURN 0;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
