CREATE OR REPLACE PROCEDURE anzics_pck.set_icu_date ( nr_seq_atepacu_p bigint) AS $body$
BEGIN
    IF (coalesce(current_setting('anzics_pck.nr_seq_atepacu_w')::bigint,0) <> nr_seq_atepacu_p) THEN
      PERFORM set_config('anzics_pck.nr_seq_atepacu_w', nr_seq_atepacu_p, false);
      SELECT MAX(dt_entrada_unidade),
        coalesce(MAX(dt_saida_unidade),clock_timestamp())
      INTO STRICT current_setting('anzics_pck.dt_inicio_w')::timestamp,
        current_setting('anzics_pck.dt_fim_w')::timestamp
      FROM atend_paciente_unidade
      WHERE nr_seq_interno        = nr_seq_atepacu_p;
      IF (current_setting('anzics_pck.dt_inicio_w')::timestamp                           - current_setting('anzics_pck.dt_fim_w')::timestamp) > 1 THEN
        PERFORM set_config('anzics_pck.dt_fim_w', current_setting('anzics_pck.dt_inicio_w')::timestamp + 1 - 1/24/60/60, false);
      END IF;
    END IF;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
