CREATE OR REPLACE FUNCTION apache_update_md_pck.obter_score_pto_idade_md (qt_ano_p bigint ) RETURNS bigint AS $body$
DECLARE

    qt_pont_idade_w bigint;

BEGIN
      if (qt_ano_p >= 75) then
        qt_pont_idade_w	:= 6;
      elsif (qt_ano_p >= 65) then
        qt_pont_idade_w	:= 5;
      elsif (qt_ano_p >= 55) then
        qt_pont_idade_w	:= 3;
      elsif (qt_ano_p >= 45) then
        qt_pont_idade_w	:= 2;
      else
        qt_pont_idade_w	:= 0;
      end if;

      return qt_pont_idade_w;
  end;


