SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE apap_dinamico_pck.atualiza_atribs_vis_grafico (nr_seq_apap_grupo_p bigint, nr_limite_max_p bigint DEFAULT 0) AS $body$
DECLARE


	qtd_linhas_visiveis_w bigint := 0;

  c_linha RECORD;

BEGIN

	SELECT COUNT(c.nr_seq_linha) qt_linhas
	  INTO STRICT qtd_linhas_visiveis_w
	  FROM TABLE( apap_dinamico_pck.get_linhas_secao_pac(nr_seq_apap_grupo_p) ) c
	 WHERE coalesce(nr_seq_superior::text, '') = ''
	   AND c.ie_exibe_grafico = 'S';

	IF qtd_linhas_visiveis_w = 0 THEN

		FOR c_linha IN (SELECT *
						 FROM ( SELECT c.nr_seq_linha nr_seq_linha
								  FROM	 TABLE( apap_dinamico_pck.get_linhas_secao_pac(nr_seq_apap_grupo_p) ) c
								 WHERE coalesce(nr_seq_superior::text, '') = ''
							  ORDER BY c.nr_seq_apresentacao,
									 c.nr_ordem_composto,
									 c.ds_informacao ) A WHERE nr_limite_max_p = 0 LIMIT (nr_limite_max_p)) LOOP

			UPDATE w_apap_pac_informacao
			   SET ie_exibe_grafico = 'S'
			 WHERE nr_sequencia = c_linha.nr_seq_linha
				OR nr_seq_superior = c_linha.nr_seq_linha;

		END LOOP;

		COMMIT;

	END IF;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
