CREATE OR REPLACE FUNCTION apap_dinamico_pck.get_dates_from_interval (dt_start timestamp, dt_end timestamp) RETURNS SETOF DATE_T AS $body$
DECLARE

	dt_r_w date_r;
	qt_days_w bigint;
BEGIN
	qt_days_w := trunc(dt_end) - trunc(dt_start);
	for days_add in 1..qt_days_w loop
		dt_r_w.dt_calc := trunc(dt_start) + days_add;
		RETURN NEXT dt_r_w;
	end loop;
   return;
END;

