CREATE OR REPLACE FUNCTION apap_dinamico_pck.get_doc_read_only ( nr_atendimento_p bigint, nr_seq_documento_p bigint) RETURNS varchar AS $body$
DECLARE


	ie_regra_setor_w varchar(1);
	ie_regra_perfil_w varchar(1);
	ie_regra_diagnostico_w varchar(1);
	ie_doc_read_only_w varchar(1);

BEGIN
	begin
	  select  coalesce(max('S'),'N')
	  into STRICT    ie_regra_setor_w
	  from    documento_setor
	  where   nr_seq_documento = nr_seq_documento_p;
	
	  select  coalesce(max('S'),'N')
	  into STRICT    ie_regra_perfil_w
	  from    documento_acesso
	  where   nr_seq_documento = nr_seq_documento_p;
	
	  select  coalesce(max('S'), 'N')
	  into STRICT    ie_regra_diagnostico_w
	  from    documento_diagnostico doc
	  where   doc.nr_seq_documento = nr_seq_documento_p
	  and     exists ( SELECT  1
					  from    diagnostico_doenca dd
					  where   dd.ie_classificacao_doenca = 'P'
					  and     dd.ie_tipo_diagnostico IN (2, 3)
					  and     coalesce(dd.ie_situacao,'A') = 'A'
					  and     (dd.dt_liberacao IS NOT NULL AND dd.dt_liberacao::text <> '')
					  and     dd.cd_doenca = doc.cd_doenca
					  and     dd.nr_atendimento = nr_atendimento_p);

			
	  if (ie_regra_setor_w = 'S') then
		if (obter_regra_acesso_apap(nr_seq_documento_p, 'S', nr_atendimento_p) = 'L') then
		  ie_doc_read_only_w := 'S';
		end if;
	  end if;

	  if (ie_regra_perfil_w = 'S') then
		if (obter_regra_acesso_apap(nr_seq_documento_p, 'P', nr_atendimento_p) = 'L') then
		  ie_doc_read_only_w := 'S';
		end if;
	  end if;

	  if (ie_regra_diagnostico_w = 'S') then
		if (obter_regra_acesso_apap(nr_seq_documento_p, 'D', nr_atendimento_p) = 'L') then
		  ie_doc_read_only_w := 'S';
		end if;
	  end if;

	exception
	  when others then
		ie_doc_read_only_w := null;
	end;

	return ie_doc_read_only_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
