CREATE OR REPLACE FUNCTION apap_dinamico_pck.obter_comentario_celula (nr_seq_valor_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

   ds_retorno_w   varchar(2000);

BEGIN
   select   substr(max(ds_comentario),1,2000)
   into STRICT     ds_retorno_w
   from     w_apap_pac_registro_hist
   where    nr_seq_valor = nr_seq_valor_p
   and		nr_atendimento	  = nr_atendimento_p
   and (coalesce(ie_situacao::text, '') = '' or ie_situacao = 'A');

   return   ds_retorno_w;
   END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
