CREATE OR REPLACE FUNCTION apap_dinamico_pck.obter_comentario (nr_seq_documento_p bigint, nr_atendimento_p bigint, nm_tabela_p text, nm_atributo_p text, dt_apap_p timestamp) RETURNS varchar AS $body$
DECLARE

   ds_retorno_w   varchar(2000);

BEGIN
   select   substr(max(ds_comentario),1,2000)
   into STRICT     ds_retorno_w
   from     w_apap_pac_registro_hist
   where    nr_seq_documento  	= nr_seq_documento_p
   and		nr_atendimento	  	= nr_atendimento_p
   and      nm_tabela         	= nm_tabela_p
   and      nm_atributo       	= nm_atributo_p
   and      dt_apap           	= dt_apap_p
   and      coalesce(ie_situacao,'A')= 'A'
   and		coalesce(ie_tipo,'C')	= 'C';

   return   ds_retorno_w;
   END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
