CREATE OR REPLACE FUNCTION apap_dinamico_pck.obter_propriedades_timeline ( dt_inicio_p timestamp, dt_fim_p timestamp, ie_range_p text, ie_intervalo_p text) RETURNS SETOF PROPRIEDADES_T AS $body$
DECLARE


propriedades_r_w  propriedades_r;
dt_inicio_w          timestamp;
dt_fim_w             timestamp;
dt_inicio_timeline_w timestamp;
dt_fim_timeline_w    timestamp;
nr_minuto_w          smallint;
ie_multiplo_w        varchar(1) := 'N';

BEGIN
dt_fim_w := dt_fim_p;
if (dt_fim_w > clock_timestamp()) then
   dt_fim_w := clock_timestamp();
end if;
dt_inicio_w := dt_fim_w - (ie_range_p/24);

if (ie_intervalo_p in ('0','5','15','30')) then
   select   PKG_DATE_UTILS.extract_field('MINUTE',dt_inicio_w,0)
   into STRICT     nr_minuto_w
;

   while(ie_multiplo_w <> 'S') loop
	  if (mod(nr_minuto_w,5) = 0) then
		 ie_multiplo_w := 'S';
	  else
		 nr_minuto_w := nr_minuto_w - 1;
	  end if;
   end loop;
   dt_inicio_timeline_w := PKG_DATE_UTILS.get_Time(dt_inicio_w,PKG_DATE_UTILS.extract_field('HOUR',dt_inicio_w,0),nr_minuto_w);
   dt_fim_timeline_w    := dt_inicio_timeline_w + (ie_range_p/24) + (2/24);
   if (dt_fim_timeline_w < clock_timestamp()) then
	  dt_fim_timeline_w := dt_inicio_timeline_w + (ie_range_p/24);
   end if;
else
   dt_inicio_timeline_w := PKG_DATE_UTILS.get_Time(dt_inicio_w,PKG_DATE_UTILS.extract_field('HOUR',dt_inicio_w,0),0);
   dt_fim_timeline_w    := dt_inicio_timeline_w + (ie_range_p/24) + (2/24);
   if (dt_fim_timeline_w < clock_timestamp()) then
	  dt_fim_timeline_w := dt_inicio_timeline_w + (ie_range_p/24);
   end if;
end if;
propriedades_r_w.dt_inicio_calendario  := trunc(dt_inicio_w);
propriedades_r_w.dt_fim_calendario     := fim_dia(dt_fim_w);
propriedades_r_w.dt_inicio_timeline    := dt_inicio_timeline_w;
propriedades_r_w.dt_fim_timeline       := dt_fim_timeline_w;
RETURN NEXT propriedades_r_w;
return;
END;

