CREATE OR REPLACE PROCEDURE apap_pck.add_totalizador_grupo ( nr_hora_p bigint, nr_dia_p bigint, vl_informacao_p bigint, ds_informacao_p text, grupo_p INOUT dadosGrupo_tp) AS $body$
DECLARE

				
nr_indice_w	integer;
BEGIN
nr_indice_w	:= grupo_p.horarios_totalizador.count +1;

for i in 1..grupo_p.horarios_totalizador.count loop
	begin
	if (grupo_p.horarios_totalizador[i].nr_hora = nr_hora_p) and (grupo_p.horarios_totalizador[i].nr_dia = nr_dia_p) then
		nr_indice_w	:= i;
		exit;
	end if;
	
	end;
end loop;
grupo_p.horarios_totalizador[nr_indice_w].nr_hora		:= nr_hora_p;
grupo_p.horarios_totalizador[nr_indice_w].nr_dia		:= nr_dia_p;
grupo_p.horarios_totalizador[nr_indice_w].ds_resultado		:= ds_informacao_p;
if (vl_informacao_p IS NOT NULL AND vl_informacao_p::text <> '') then
	grupo_p.horarios_totalizador[nr_indice_w].vl_resultado		:= vl_informacao_p + coalesce(grupo_p.horarios_totalizador[nr_indice_w].vl_resultado,0);
end if;

END;

$body$
