SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE apap_pck.processar_dados_horarios_adic ( t_objeto_row_p INOUT t_objeto_row, informacao_p INOUT dadosInformacao_tp) AS $body$
DECLARE

ds_dados_horarios_w	varchar(4000);
ds_coluna_w		varchar(255);
BEGIN

for i in 1..informacao_p.horarios.count loop
	begin
	if (informacao_p.horarios[i].ie_outros_registros = 'S') then
	    ds_coluna_w	:= 'DS_DIA_'||trim(both to_char(informacao_p.horarios[i].nr_dia,'00'))||'_HORA_'||trim(both to_char(informacao_p.horarios[i].nr_hora,'00'))||';';
	    if (coalesce(position(ds_coluna_w in ds_dados_horarios_w),0)	= 0) then
		ds_dados_horarios_w	:= ds_dados_horarios_w||ds_coluna_w;
	    end if;
	
	end if;
	
	
	end;
end loop;

t_objeto_row_p.ds_dados_horarios	:= ds_dados_horarios_w;

end;



$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
