CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_atualizar_atend_proc_imp ( nr_seq_lote_atend_imp_p audc_lote_atendimento_imp.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE



cd_procedimento_w	procedimento.cd_procedimento%type;
ie_origem_proced_w	procedimento.ie_origem_proced%type;


C01 CURSOR(  nr_seq_lote_atend_imp_pc	audc_lote_atendimento_imp.nr_sequencia%type ) FOR
	SELECT  nr_sequencia,
	        nr_seq_prestador_solic,
		nr_seq_guia,
		cd_estabelecimento
	from    audc_atendimento_imp
	where	nr_seq_lote_atend_imp = nr_seq_lote_atend_imp_pc;

C02 CURSOR( nr_seq_audc_atend_imp_pc  audc_atendimento_imp.nr_sequencia%type ) FOR
	SELECT 	nr_sequencia,
		cd_procedimento,
		cd_tabela
	from	audc_atend_proc_imp
	where	nr_seq_audc_atend_imp = nr_seq_audc_atend_imp_pc;

BEGIN


for c01_w in C01( nr_seq_lote_atend_imp_p ) loop

	for c02_w in C02( c01_w.nr_sequencia ) loop


		begin
			select 	cd_procedimento,
				ie_origem_proced
			into STRICT	cd_procedimento_w,
				ie_origem_proced_w
			from	pls_guia_plano_proc
			where	nr_seq_guia = C01_w.nr_seq_guia
			and	cd_procedimento = C02_w.cd_procedimento  LIMIT 1;

		exception when no_data_found then
			cd_procedimento_w := null;
			ie_origem_proced_w := null;
		end;

		if ( coalesce(ie_origem_proced_w::text, '') = '' ) then
			--Retorna o codigo e a origem do procedimento ja convertidos

			pls_gerar_dados_guia_proc_imp(  C02_w.cd_procedimento, C02_w.cd_tabela, C01_w.nr_seq_prestador_solic, C01_w.cd_estabelecimento, cd_procedimento_w, ie_origem_proced_w );
		end if;


		update	audc_atend_proc_imp
		set	ie_origem_proced = coalesce(ie_origem_proced, ie_origem_proced_w),
			cd_procedimento = coalesce(cd_procedimento, cd_procedimento_w),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = c02_w.nr_sequencia;
