CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_gerar_grupo_auditor ( nr_seq_lote_atend_imp_p audc_lote_atendimento_imp.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE



C01 CURSOR(  nr_seq_lote_atend_imp_pc	audc_lote_atendimento_imp.nr_sequencia%type ) FOR
	SELECT 	nr_sequencia
	from 	audc_atendimento a
	where	a.nr_seq_lote_atend_imp = nr_seq_lote_atend_imp_pc;

C02 CURSOR(  nr_seq_audc_atend_pc	audc_atendimento_imp.nr_sequencia%type ) FOR
	SELECT  a.nr_seq_grupo,
		a.nm_usuario_exec,
	        b.nr_prioridade
	from    audc_regra_eleg_grupo_aud a,
		audc_regra_elegibilidade b,
		audc_atend_elegibilidade c
	where   a.nr_seq_regra_eleg = b.nr_sequencia
	and	c.nr_seq_regra_eleg = b.nr_sequencia
	and	c.nr_seq_regra_eleg = a.nr_seq_regra_eleg
	and     c.nr_seq_audc_atend = nr_seq_audc_atend_pc
	order by b.nr_prioridade asc;
	--Nao alterar esta ordenacao


BEGIN

for c01_w in C01( nr_seq_lote_atend_imp_p ) loop
	for c02_w in C02( c01_w.nr_sequencia ) loop

		insert into audc_atend_grupo_aud(
			nr_sequencia, nr_seq_grupo,nr_seq_audc_atend,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, nm_usuario_exec)
		values ( nextval('audc_atend_grupo_aud_seq'), c02_w.nr_seq_grupo, c01_w.nr_sequencia,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			nm_usuario_p, c02_w.nm_usuario_exec);

		update 	audc_atendimento
		set	nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			nr_prioridade = c02_w.nr_prioridade
		where	nr_sequencia = 	c01_w.nr_sequencia;
	end loop;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
