CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_valida_eleg_prestador ( nr_seq_regra_eleg_p audc_regra_elegibilidade.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, ie_gera_regra_p INOUT text ) AS $body$
DECLARE



ie_gera_regra_w		varchar(1) := 'S';
ie_grupo_valido_w 	varchar(1) := 'S';

C01 CURSOR( nr_seq_regra_eleg_pc	audc_regra_elegibilidade.nr_sequencia%type) FOR
	SELECT  nr_sequencia,
        	nr_seq_prestador_solic,
		nr_seq_grupo_prestador
	from    audc_regra_eleg_prestador
	where   nr_seq_regra_eleg = nr_seq_regra_eleg_pc
	and     ie_situacao = 'A';


BEGIN


for c01_w in C01( nr_seq_regra_eleg_p ) loop

	ie_gera_regra_w := 'N';


	if ( coalesce(c01_w.nr_seq_prestador_solic::text, '') = '' or nr_seq_prestador_p = c01_w.nr_seq_prestador_solic  ) then

		if (c01_w.nr_seq_grupo_prestador IS NOT NULL AND c01_w.nr_seq_grupo_prestador::text <> '') then
			ie_grupo_valido_w := pls_se_grupo_preco_prestador(c01_w.nr_seq_grupo_prestador, nr_seq_prestador_p, null);
		end if;

		if ( ie_grupo_valido_w = 'S' ) then
			ie_gera_regra_w := 'S';
			exit;
		end if;
	end if;
end loop;

ie_gera_regra_p := ie_gera_regra_w;

END;

$body$
