CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_valida_eleg_procedimento ( nr_seq_regra_eleg_p audc_regra_elegibilidade.nr_sequencia%type, nr_seq_audc_atend_imp_p audc_atendimento_imp.nr_sequencia%type, ie_gera_regra_p INOUT text ) AS $body$
DECLARE



ie_gera_regra_w		varchar(1) := 'S';
ie_grupo_valido_w 	varchar(1) := 'S';


C01 CURSOR( nr_seq_regra_eleg_pc	audc_regra_elegibilidade.nr_sequencia%type) FOR
	SELECT  cd_procedimento,
	        ie_origem_proced,
	        nr_seq_grupo_servico
	from    audc_regra_eleg_servico
	where   nr_seq_regra_eleg = nr_seq_regra_eleg_pc
	and     ie_situacao = 'A';

C02 CURSOR( nr_seq_audc_atend_imp_pc	audc_atendimento_imp.nr_sequencia%type ) FOR
	SELECT  cd_procedimento,
		ie_origem_proced
	from    audc_atend_proc_imp
	where   nr_seq_audc_atend_imp = nr_seq_audc_atend_imp_pc;


BEGIN

for c01_w in C01( nr_seq_regra_eleg_p ) loop
	ie_gera_regra_w := 'N';

	for c02_w in C02( nr_seq_audc_atend_imp_p ) loop
		ie_gera_regra_w := 'N';

		if (c01_w.cd_procedimento IS NOT NULL AND c01_w.cd_procedimento::text <> '' AND c01_w.cd_procedimento = c02_w.cd_procedimento and c01_w.ie_origem_proced = c02_w.ie_origem_proced ) then
			ie_gera_regra_w := 'S';
		end if;

		if (c01_w.nr_seq_grupo_servico IS NOT NULL AND c01_w.nr_seq_grupo_servico::text <> '') then
			ie_gera_regra_w := 'N';
			ie_grupo_valido_w := pls_se_grupo_preco_servico( c01_w.nr_seq_grupo_servico, c02_w.cd_procedimento, c02_w.ie_origem_proced );

			if ( ie_grupo_valido_w = 'S' ) then
				ie_gera_regra_w := 'S';
			end if;
		end if;

		if ( ie_gera_regra_w = 'S' ) then
			exit;
		end if;
	end loop;

	if ( ie_gera_regra_w = 'S' ) then
		exit;
	end if;
end loop;

ie_gera_regra_p := ie_gera_regra_w;

END;

$body$
