SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_valida_eleg_promoprev ( nr_seq_regra_eleg_p audc_regra_elegibilidade.nr_sequencia%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, ie_gera_regra_p INOUT text ) AS $body$
DECLARE



ie_gera_regra_w		varchar(1) := 'S';
ie_grupo_valido_w 	varchar(1) := 'S';

qt_home_care_w		integer;
qt_programa_w		integer;

C01 CURSOR( nr_seq_regra_eleg_pc	audc_regra_elegibilidade.nr_sequencia%type) FOR
	SELECT  ie_benef_home_care,
          	nr_seq_mprev_programa
	from    audc_regra_eleg_promoprev
	where   nr_seq_regra_eleg = nr_seq_regra_eleg_pc
	and     ie_situacao = 'A';


BEGIN

for c01_w in C01( nr_seq_regra_eleg_p ) loop

	ie_gera_regra_w := 'N';


	if ( c01_w.ie_benef_home_care = 'S') then
		select  count(1)
		into STRICT	qt_home_care_w
		from    paciente_home_care a
 		where   coalesce(a.dt_final::text, '') = ''
  		and     a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;


	if (c01_w.nr_seq_mprev_programa IS NOT NULL AND c01_w.nr_seq_mprev_programa::text <> '') then
		select  count(1)
		into STRICT	qt_programa_w
  		from    mprev_participante a,
       			mprev_programa_partic b
  		where   b.nr_seq_participante = a.nr_sequencia
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	b.nr_seq_programa = c01_w.nr_seq_mprev_programa
 		and    	b.dt_inclusao <= clock_timestamp()
		and (coalesce(b.dt_exclusao::text, '') = '' or b.dt_exclusao >= clock_timestamp());
	end if;


	if ( c01_w.ie_benef_home_care = 'S' and  (c01_w.nr_seq_mprev_programa IS NOT NULL AND c01_w.nr_seq_mprev_programa::text <> '')) then
		if ( qt_home_care_w > 0 and qt_programa_w > 0 ) then
			ie_gera_regra_w := 'S';
		end if;

	elsif ( coalesce(c01_w.ie_benef_home_care, 'N') = 'N' and  (c01_w.nr_seq_mprev_programa IS NOT NULL AND c01_w.nr_seq_mprev_programa::text <> '')) then
		if ( qt_programa_w > 0 ) then
			ie_gera_regra_w := 'S';
		end if;
	elsif ( c01_w.ie_benef_home_care = 'S' and  coalesce(c01_w.nr_seq_mprev_programa::text, '') = '') then
		if ( qt_home_care_w > 0 ) then
			ie_gera_regra_w := 'S';
		end if;
	end if;
end loop;

ie_gera_regra_p := ie_gera_regra_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
