SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE audc_auditoria_concorrente_pck.audc_valida_permissao ( cd_cgc_prestador_p pessoa_juridica.cd_cgc%type, cd_cpf_prestador_p pessoa_fisica.nr_cpf%type, cd_prestador_p pls_prestador.cd_prestador%type, cd_ans_p pls_outorgante.cd_ans%type, nr_seq_prestador_p INOUT pls_prestador.nr_sequencia%type, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_seq_prestador_w	pls_prestador.nr_sequencia%type;
qt_prestador_w		integer(5) := 0;
ds_retorno_w		varchar(255);
					

BEGIN

select 	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from 	pls_outorgante
where	cd_ans = cd_ans_p;


if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then
	
	nr_seq_prestador_w := pls_obter_prestador_imp(cd_cgc_prestador_p, cd_cpf_prestador_p, cd_prestador_p, null, null,null,'G', cd_estabelecimento_w, null);
	
	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then
		select  count(1)
		into STRICT	qt_prestador_w
		from    audc_prestador a
		where   a.ie_situacao = 'A'
		and     a.nr_seq_prestador = nr_seq_prestador_w;
		
		if ( qt_prestador_w = 0 ) then
			select  count(1)
			into STRICT	qt_prestador_w
			from    audc_prestador a,
        			pls_preco_prestador b
			where   b.nr_seq_grupo = a.nr_seq_grupo_prestador
			and     a.ie_situacao = 'A'
			and     b.nr_seq_prestador = nr_seq_prestador_w;
		end if;
	else
		ds_retorno_w := wsuite_util_pck.get_wsuite_expression_domain(9479, 'C002', cd_estabelecimento_w);
	end if;	
else 	
	ds_retorno_w := wsuite_util_pck.get_wsuite_expression_domain(9479, 'C004', cd_estabelecimento_w);

end if;


if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	if ( qt_prestador_w = 0 ) then		
		ds_retorno_w := wsuite_util_pck.get_wsuite_expression_domain(9479, 'C005', cd_estabelecimento_w);
	end if;
end if;

nr_seq_prestador_p 	:= nr_seq_prestador_w;
ds_retorno_p		:= ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
