SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE audio_video_pck.change_privacity (nm_usuario_p text, nr_atendimento_p bigint) AS $body$
DECLARE

    ie_privacidade_w varchar(1);
    is_exist_w       smallint;

BEGIN
        select case
                   when exists (select 0 from atend_paciente_adic where nr_atendimento = nr_atendimento_p)
                   then 1
                   else 0
               end into STRICT is_exist_w
;

        if (is_exist_w = 1) then
			begin
				select ie_privacidade_av into STRICT ie_privacidade_w from atend_paciente_adic where nr_atendimento = nr_atendimento_p;

				if (coalesce(ie_privacidade_w::text, '') = '' or ie_privacidade_w = 'N') then
					update atend_paciente_adic
						set ie_privacidade_av = 'S'
					where nr_atendimento = nr_atendimento_p;
				else
					update atend_paciente_adic
						set ie_privacidade_av = 'N'
					where nr_atendimento = nr_atendimento_p;
				end if;
			end;
        else
			begin
				insert into atend_paciente_adic(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_atendimento,
						ie_privacidade_av)
				values (
						nextval('atend_paciente_adic_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_atendimento_p,
						'S');
			end;
        end if;
        commit;
    end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
