CREATE OR REPLACE FUNCTION bifrost.sendintegrationpolling ( direct boolean, eventName text, javaClass text, content text, synchronous char, userName text, usesTasyBackend char, headers text ) RETURNS text AS $body$
DECLARE

    resultValue text := 'NACK';
	pollingConsumer varchar(100) := null;
	tenantId varchar(1024);
	consumerName varchar(100);

BEGIN

	tenantId := bifrost.gettenantid();

	consumerName := obter_valor_param_usuario(9041, 13, 0, coalesce(wheb_usuario_pck.get_nm_usuario, userName), wheb_usuario_pck.get_cd_estabelecimento);

	if consumerName = '' or coalesce(consumerName::text, '') = '' then
		select 'default' into STRICT consumerName;
	end if;

	if direct then
		INSERT INTO TIE_INBOUND(STATUS, CONSUMER, EVENT, TENANT_ID, HEADERS, PAYLOAD) VALUES ('A', consumerName, eventName, tenantId, headers, content);
	else
		INSERT INTO TIE_INBOUND(STATUS, CONSUMER, EVENT, TENANT_ID, HEADERS, MESSAGE_CALLBACK, MESSAGE_ARGUMENTS) VALUES ('A', consumerName, eventName, tenantId, headers, javaClass, content);
	end if;
	commit;

	select 'ACK' into STRICT resultValue;

    return resultValue;

END;

