CREATE OR REPLACE FUNCTION bifrost_icca.gettenantid () RETURNS varchar AS $body$
DECLARE

    envConfig varchar(1024);
    tenant_config_exists integer := 0;

BEGIN

    SELECT COALESCE(
        (SELECT 1
            WHERE EXISTS (SELECT 1 FROM ENVIRONMENT_CONFIGURATIONS WHERE configuration_key = 'philips.cloud.tenant.id')
        ),0
    ) INTO STRICT tenant_config_exists;

    if tenant_config_exists = 1 then
        select configuration_value into STRICT envConfig from ENVIRONMENT_CONFIGURATIONS where configuration_key = 'philips.cloud.tenant.id';
        return envConfig;
    else
        return null;
    end if;

exception when no_data_found then
    return null;
END;

$body$
LANGUAGE PLPGSQL
;
