SET client_encoding TO 'UTF8';


    /** 
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Delete the file from Error folder for outbound message
    Usage of this function:- while resending the message delete the file from error folder and after correction sent back to main folder
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
   **/
CREATE OR REPLACE PROCEDURE carestream_japan_l10n_pck.delete_error_file (ds_file_p text, log_file_name_w text, message_type_p text) AS $body$
DECLARE

    cd_event_w philips_json;

BEGIN
        select  carestream_japan_l10n_pck.get_directory_event_code(message_type_p)
        into STRICT   cd_event_w
;
        CALL jpn_utl_file_pck.delete_file(cd_event_w.get['cd_event_index_error'].value_of(),ds_file_p); -- delete the file from index_error_folder
        CALL jpn_utl_file_pck.delete_file(cd_event_w.get['cd_event_data_error'].value_of(),ds_file_p); -- delete the file from data_error_folder
        PERFORM set_config('carestream_japan_l10n_pck.ds_log_message_w', 'Successfully created and moved the file from error folder: '||ds_file_p||', '||'Time of Transmission = '||to_char(clock_timestamp(),'hh24:mi:ss'), false);
        CALL CALL carestream_japan_l10n_pck.save_log(cd_event_w.get['cd_event_log'].value_of(), log_file_name_w, current_setting('carestream_japan_l10n_pck.ds_log_message_w')::varchar(32767)); -- updating log file
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carestream_japan_l10n_pck.delete_error_file (ds_file_p text, log_file_name_w text, message_type_p text) FROM PUBLIC;
