SET client_encoding TO 'UTF8';


    
	/**
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Others information of patient 
	unique_key_p - encounter_number
	-- It contains the patient's   aid classification, fetched from EPC need for nursing care. 
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	**/
CREATE OR REPLACE PROCEDURE carestream_japan_l10n_pck.patient_aid_classification (unique_key_p text, message_data INOUT text) AS $body$
DECLARE

	c20 CURSOR FOR
       SELECT  max(a.nr_sequencia) nr_sequencia,
                coalesce(a.ie_auxilio_medico,' ') aid_classification
        from    necessidade_auxilio_med a
        where   a.nr_atendimento = unique_key_p 
        group by a.nr_atendimento, a.nr_sequencia, a.ie_auxilio_medico;

        r_c20    c20%rowtype;

BEGIN
        PERFORM set_config('carestream_japan_l10n_pck.ds_line_w', null, false);
        open c20;
		fetch c20 into r_c20;
        if (c20%NOTFOUND) then
            CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(' ', 1); -- IDO_KBN
        else
			CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_c20.AID_CLASSIFICATION, 1); -- IDO_KBN
        end if;
        close c20;
		message_data := current_setting('carestream_japan_l10n_pck.ds_line_w')::varchar(32767);


    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carestream_japan_l10n_pck.patient_aid_classification (unique_key_p text, message_data INOUT text) FROM PUBLIC;
