SET client_encoding TO 'UTF8';


	/**
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Body equipment information  OCCURS(n)
	unique_key_p - patient id
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	**/
CREATE OR REPLACE PROCEDURE carestream_japan_l10n_pck.patient_body_equipment_info (unique_key_p text, message_data INOUT text) AS $body$
DECLARE

	c06 CURSOR FOR
		SELECT count(*) over () total_count_row,
			CASE WHEN coalesce(a.nr_seq_acessorio::text, '') = '' THEN ' '  ELSE a.nr_seq_acessorio END  body_orthosis_code,
			coalesce(to_char(a.dt_registro, 'YYYYMMDD'), ' ') body_orthosis_reg_date,
			coalesce(b.ds_acessorio, ' ') body_orthosis_name,
			case
			when(a.ie_situacao = 'A' and (coalesce(a.dt_fim::text, '') = '')) then '+'
			when(a.ie_situacao = 'A' and (a.dt_fim) <= clock_timestamp()) then '-'
            else ' '
			end body_orthosis_status
		from paciente_acessorio a left outer join acessorio_paciente b
		on  a.nr_seq_acessorio = b.nr_sequencia
		where b.ie_situacao = 'A'
			and  (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			and coalesce(dt_inativacao::text, '') = ''
			and  a.cd_pessoa_fisica = unique_key_p;

		r_count c06%ROWTYPE;

        
BEGIN 
            PERFORM set_config('carestream_japan_l10n_pck.ds_line_w', null, false);

            open c06;
            fetch c06 into r_count;
            if (c06%NOTFOUND) then
             CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(0, 5, 'L', '0'); -- SS_NUM
             else
             CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_count.total_count_row, 5, 'L', '0'); -- SS_NUM
             end if;
            close c06;

            for r_c06 in c06 loop
                begin
                    CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_c06.body_orthosis_code, 4, 'L', '0'); --  SS_CD
                    CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_c06.body_orthosis_reg_date, 8); -- LAST_IN_DATE
                    CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_c06.body_orthosis_name, 20, 'R', ' '); -- SS_NAME
                    CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_japan_l10n_pck.append_text(r_c06.body_orthosis_status, 1); -- VALUE
                end;
            end loop;
            message_data := current_setting('carestream_japan_l10n_pck.ds_line_w')::varchar(32767);
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carestream_japan_l10n_pck.patient_body_equipment_info (unique_key_p text, message_data INOUT text) FROM PUBLIC;
