SET client_encoding TO 'UTF8';


	/** 
   +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Patient Change Information
	unique_key :- encounter number
	message type:- 1G -> Patient change information 
	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
   **/
CREATE OR REPLACE PROCEDURE carestream_phys_japan_l10n_pck.patient_change_info ( unique_key_p bigint, nm_usuario_p text, log_sequence_number_p text, file_name_p text, cd_establishment_p bigint, ie_retransmit_p text ) AS $body$
BEGIN
        PERFORM set_config('carestream_phys_japan_l10n_pck.ds_line_w', null, false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.ds_local_w', null, false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.message_type_w', '1G', false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.data_file_name_w', null, false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.file_seq_directory_w', 'CARE_PHY_PDATA', false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.log_sequence_number_w', log_sequence_number_p, false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.ds_message_w', 'Patient Change Information', false);
        PERFORM set_config('carestream_phys_japan_l10n_pck.interface_event_w', 937, false);

        select obter_paciente_prontuario(unique_key_p, 'C')
        into STRICT current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10)
;

        select carestream_japan_l10n_pck.get_directory_event_code(current_setting('carestream_phys_japan_l10n_pck.message_type_w')::varchar(2))
        into STRICT   current_setting('carestream_phys_japan_l10n_pck.cd_integartion_event_w')::philips_json
;

        select carestream_japan_l10n_pck.get_log_file_name(current_setting('carestream_phys_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['control_code'].value_of(), current_setting('carestream_phys_japan_l10n_pck.physiology_dept_code_w')::varchar(2), current_setting('carestream_phys_japan_l10n_pck.his_system_code')::varchar(2))
        into STRICT current_setting('carestream_phys_japan_l10n_pck.log_file_name_w')::varchar(100)
;

        -- retrieving the event and company name for interface  
        SELECT * FROM carestream_japan_l10n_pck.get_interface_event_details(current_setting('carestream_phys_japan_l10n_pck.interface_event_w')::integer, current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255), current_setting('carestream_phys_japan_l10n_pck.nm_interface_w')::varchar(255)) INTO STRICT current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255), current_setting('carestream_phys_japan_l10n_pck.nm_interface_w')::varchar(255);

		--creating message for integration
        begin
        SELECT * FROM carestream_japan_l10n_pck.carestream_header(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.message_type_w')::varchar(2), current_setting('carestream_phys_japan_l10n_pck.physiology_dept_code_w')::varchar(2), 1, current_setting('carestream_phys_japan_l10n_pck.ds_line_w')::varchar(32767), current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10)) INTO STRICT current_setting('carestream_phys_japan_l10n_pck.ds_line_w')::varchar(32767), current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10);

        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_basic_info(current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_allergy_info(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_disabilities_info(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_physical_motility_info(current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_body_equipment_info(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_risk_info(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_pregnant_info(current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_exam_result_info(current_setting('carestream_phys_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767)); -- KEKKA1,KEKKA1_DATE 1....15 UNUSED
        current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767) := carestream_japan_l10n_pck.patient_infection_info(current_setting('carestream_phys_japan_l10n_pck.encounter_id_w')::varchar(10), current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));
        CALL CALL carestream_phys_japan_l10n_pck.append_message(current_setting('carestream_phys_japan_l10n_pck.ds_local_w')::varchar(32767));

		exception
            when others then
                PERFORM set_config('carestream_phys_japan_l10n_pck.ds_log_message_w', 'Error occured while sending the integration message for'
                                    || current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255) || chr(10)
                                    || 'Integration name - japan_carestream_ris('
                                    || current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255)
                                    || ').'
                                    || chr(10)
                                    || 'Message Type:'
                                    || current_setting('carestream_phys_japan_l10n_pck.message_type_w')::varchar(2)
                                    || chr(10)
                                    || 'Time of faliure : '
                                    || clock_timestamp() || chr(10)
                                    || 'Patient MRN:'
                                    || unique_key_p, false);

                record_integration_call_log(nm_usuario_p, nm_usuario_p, clock_timestamp(), current_setting('carestream_phys_japan_l10n_pck.nm_interface_w')::varchar(255), current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255),
                                            'F', 'E', null, current_setting('carestream_phys_japan_l10n_pck.ds_log_message_w')::varchar(32767), current_setting('carestream_phys_japan_l10n_pck.ds_line_w')::varchar(32767),
                                            current_setting('carestream_phys_japan_l10n_pck.ds_log_message_w')::varchar(32767), current_setting('carestream_phys_japan_l10n_pck.data_file_name_w')::varchar(100), current_setting('carestream_phys_japan_l10n_pck.log_sequence_number_w')::integration_message_log.nr_seq_int_call_log%type, current_setting('carestream_phys_japan_l10n_pck.patient_mrn_w')::bigint, current_setting('carestream_phys_japan_l10n_pck.interface_event_w')::integer);

                CALL carestream_japan_l10n_pck.save_log(current_setting('carestream_phys_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['cd_event_log'].value_of(), current_setting('carestream_phys_japan_l10n_pck.log_file_name_w')::varchar(100), current_setting('carestream_phys_japan_l10n_pck.ds_log_message_w')::varchar(32767));
                return;
        end;
        -- generating the file name for first time integration else assign the existing one while resending the same message 
        if (file_name_p IS NOT NULL AND file_name_p::text <> '') then
            PERFORM set_config('carestream_phys_japan_l10n_pck.data_file_name_w', file_name_p, false);
        else
            generate_int_serial_number(0, current_setting('carestream_phys_japan_l10n_pck.file_seq_directory_w')::varchar(15), cd_establishment_p, nm_usuario_p, current_setting('carestream_phys_japan_l10n_pck.file_sequence_w')::smallint, current_setting('carestream_phys_japan_l10n_pck.interface_event_w')::integer);
            select carestream_japan_l10n_pck.get_file_name(current_setting('carestream_phys_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['control_code'].value_of(), current_setting('carestream_phys_japan_l10n_pck.physiology_dept_code_w')::varchar(2), current_setting('carestream_phys_japan_l10n_pck.his_system_code')::varchar(2), current_setting('carestream_phys_japan_l10n_pck.file_sequence_w')::smallint)
            into STRICT current_setting('carestream_phys_japan_l10n_pck.data_file_name_w')::varchar(100)
;

        end if;

        -- it is helpful while resend the information
        PERFORM set_config('carestream_phys_japan_l10n_pck.ds_script_w', 'carestream_phys_japan_l10n_pck.patient_change_info ('
                       || unique_key_p
                       || ', '''
                       || ':nm_exec_user' 
                       || ''' , '
                       || ':nr_seq_int_call_log'
                       || ', '''
                       || current_setting('carestream_phys_japan_l10n_pck.data_file_name_w')::varchar(100)
                       || ''' ,'
                       || cd_establishment_p
                       || ', ''S'');', false);

        CALL CALL carestream_phys_japan_l10n_pck.perform_integration(unique_key_p, current_setting('carestream_phys_japan_l10n_pck.message_type_w')::varchar(2), nm_usuario_p, log_sequence_number_p, current_setting('carestream_phys_japan_l10n_pck.data_file_name_w')::varchar(100),
                            current_setting('carestream_phys_japan_l10n_pck.log_file_name_w')::varchar(100), cd_establishment_p, current_setting('carestream_phys_japan_l10n_pck.file_seq_directory_w')::varchar(15), current_setting('carestream_phys_japan_l10n_pck.cd_integartion_event_w')::philips_json, current_setting('carestream_phys_japan_l10n_pck.ds_message_w')::varchar(255),
                            current_setting('carestream_phys_japan_l10n_pck.ds_script_w')::integration_call_log.ds_resend_script%type, current_setting('carestream_phys_japan_l10n_pck.nm_interface_w')::varchar(255), current_setting('carestream_phys_japan_l10n_pck.interface_event_w')::integer, current_setting('carestream_phys_japan_l10n_pck.patient_mrn_w')::bigint, ie_retransmit_p);
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carestream_phys_japan_l10n_pck.patient_change_info ( unique_key_p bigint, nm_usuario_p text, log_sequence_number_p text, file_name_p text, cd_establishment_p bigint, ie_retransmit_p text ) FROM PUBLIC;
