SET client_encoding TO 'UTF8';


   /** 
   +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Patient Discharge Information
	unique_key :- encounter number
	message type:- 3K -> Discharge confirm information
				   3L -> Discharge cancellation information 
	-- This message contains a common header and discharge information respective to patient.
	-- Based on 3K/3L if (3K , then send 1 - disharge confirm, if cancel the transfer ie 3L send 2-Admission as cancel d discharge)
    ** for first time log_sequence_number_p should be 0 and file_name_p should be null

	+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
   **/
CREATE OR REPLACE PROCEDURE carestream_ris_japan_l10n_pck.patient_discharge_info ( unique_key_p bigint, message_type_p text, processing_type_p text, nm_usuario_p text, log_sequence_number_p text default 0, file_name_p text default null, cd_establishment_p bigint default 1, ie_retransmit_p text  DEFAULT NULL) AS $body$
DECLARE


        c01 CURSOR FOR

        SELECT obter_pessoa_atendimento(a.encounter_id, 'C') patient_id,
               CASE WHEN message_type_p='3L' THEN  '2' WHEN message_type_p='3K' THEN  '1' END  encounter_type,
               coalesce(to_char(a.encounter_discharge_date,'YYYYMMDD'), ' ')  discharge_date,
               coalesce(to_char(a.encounter_discharge_date,'HH24MISS'), ' ')  discharge_time,
               (SELECT  coalesce(to_char(x.dt_previsto_alta ,'YYYYMMDD'), ' ')
                    from atend_previsao_alta x
                    where x.nr_atendimento = unique_key_p 
                    and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
                    and coalesce(dt_inativacao::text, '') = '') estimated_discharge_date,
               CASE WHEN message_type_p='3K' THEN  lpad(trim(both a.type_discharge_date), 4, 0)   ELSE ' ' END   discharge_reason,
               CASE WHEN message_type_p='3L' THEN  lpad(trim(both a.department_id), 3, 0)  ELSE ' ' END   ward_code,
               CASE WHEN message_type_p='3L' THEN  lpad(trim(both a.room_id), 4, 0)  ELSE ' ' END    room_code
        from   bft_encounter_v a
        where  a.encounter_id = unique_key_p;


BEGIN
        PERFORM set_config('carestream_ris_japan_l10n_pck.ds_line_w', null, false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.nm_interface_w', null, false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.data_file_name_w', null, false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.file_sequence_w', null, false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.ds_message_w', 'Patient Discharge Information', false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.file_seq_directory_w', 'CARE_RIS_PDATA', false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.log_sequence_number_w', log_sequence_number_p, false);
        PERFORM set_config('carestream_ris_japan_l10n_pck.interface_event_w', 934, false);

        select is_integration_exist(931, m.patient_mrn),
               m.patient_mrn
        into STRICT   current_setting('carestream_ris_japan_l10n_pck.ie_exist_w')::varchar(1),
               current_setting('carestream_ris_japan_l10n_pck.patient_mrn_w')::varchar(10)
        from (SELECT obter_prontuario_atendimento(unique_key_p) patient_mrn
                ) m;

        if (current_setting('carestream_ris_japan_l10n_pck.ie_exist_w')::varchar(1) = 'N')then
            return;
        end if;

        SELECT * FROM carestream_japan_l10n_pck.get_interface_event_details(current_setting('carestream_ris_japan_l10n_pck.interface_event_w')::integer, current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255), current_setting('carestream_ris_japan_l10n_pck.nm_interface_w')::varchar(255)) INTO STRICT current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255), current_setting('carestream_ris_japan_l10n_pck.nm_interface_w')::varchar(255);

        select carestream_japan_l10n_pck.get_directory_event_code(message_type_p)
        into STRICT   current_setting('carestream_ris_japan_l10n_pck.cd_integartion_event_w')::philips_json
;

        select carestream_japan_l10n_pck.get_log_file_name(current_setting('carestream_ris_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['control_code'].value_of(), current_setting('carestream_ris_japan_l10n_pck.radiology_dept_code_w')::varchar(2), current_setting('carestream_ris_japan_l10n_pck.his_system_code')::varchar(2))
        into STRICT   current_setting('carestream_ris_japan_l10n_pck.log_file_name_w')::varchar(100)
;

        begin
            SELECT * FROM carestream_japan_l10n_pck.carestream_header(unique_key_p, message_type_p, current_setting('carestream_ris_japan_l10n_pck.radiology_dept_code_w')::varchar(2), processing_type_p, current_setting('carestream_ris_japan_l10n_pck.ds_line_w')::varchar(32767), current_setting('carestream_ris_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_ris_japan_l10n_pck.encounter_id_w')::varchar(10)) INTO STRICT current_setting('carestream_ris_japan_l10n_pck.ds_line_w')::varchar(32767), current_setting('carestream_ris_japan_l10n_pck.patient_id_w')::varchar(10), current_setting('carestream_ris_japan_l10n_pck.encounter_id_w')::varchar(10);

            for r_c01 in c01 loop begin
				if (message_type_p = '3L') then
					r_c01.discharge_reason := '    ';
				end if;
                PERFORM set_config('carestream_ris_japan_l10n_pck.patient_id_w', r_c01.patient_id, false);
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.discharge_date, 8, 'L'); --SYORI_DATE
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.discharge_time, 6, 'L'); --SYORI_TIME
                --message body info
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(current_setting('carestream_ris_japan_l10n_pck.patient_mrn_w')::varchar(10), 10, 'L', '0'); -- PT_ID
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.encounter_type, 1, 'L'); --NYUGAI_KBN
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(coalesce(r_c01.estimated_discharge_date,  '10000101'), 8, 'L'); -- OUT_DATE
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.discharge_reason, 4); -- LVE_REASON_CD
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.ward_code, 3); -- BYOTO_CD
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(r_c01.room_code, 4); -- ROOM_CD
                CALL CALL CALL CALL CALL CALL CALL CALL CALL CALL carestream_ris_japan_l10n_pck.append_text(' ', 20, 'L'); -- FILLER
            end;
            end loop;

        exception
            when others then
                PERFORM set_config('carestream_ris_japan_l10n_pck.ds_log_message_w', 'Error occured while sending the integration message for'
                                    || current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255) || chr(10)
                                    || 'Integration name - japan_carestream_ris('
                                    || current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255)
                                    || ').'
                                    || chr(10)
                                    || 'Message Type:'
                                    || message_type_p
                                    || chr(10)
                                    || 'Time of faliure : '
                                    || clock_timestamp() || chr(10)
                                    || 'Encounter number :'
                                    || unique_key_p || chr(10)
                                    || 'ERROR : '
                                    || SQLERRM, false);

                record_integration_call_log(nm_usuario_p, nm_usuario_p, clock_timestamp(), current_setting('carestream_ris_japan_l10n_pck.nm_interface_w')::varchar(255), current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255),
                                            'F', 'E', null, current_setting('carestream_ris_japan_l10n_pck.ds_log_message_w')::varchar(32767), current_setting('carestream_ris_japan_l10n_pck.ds_line_w')::varchar(32767),
                                            current_setting('carestream_ris_japan_l10n_pck.ds_log_message_w')::varchar(32767), current_setting('carestream_ris_japan_l10n_pck.data_file_name_w')::varchar(100), current_setting('carestream_ris_japan_l10n_pck.log_sequence_number_w')::integration_message_log.nr_seq_int_call_log%type, current_setting('carestream_ris_japan_l10n_pck.patient_mrn_w')::varchar(10), current_setting('carestream_ris_japan_l10n_pck.interface_event_w')::integer);

                CALL carestream_japan_l10n_pck.save_log(current_setting('carestream_ris_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['cd_event_log'].value_of(), current_setting('carestream_ris_japan_l10n_pck.log_file_name_w')::varchar(100), current_setting('carestream_ris_japan_l10n_pck.ds_log_message_w')::varchar(32767));
                return;
        end;

        if (file_name_p IS NOT NULL AND file_name_p::text <> '') then
            PERFORM set_config('carestream_ris_japan_l10n_pck.data_file_name_w', file_name_p, false);
        else
            generate_int_serial_number(0, current_setting('carestream_ris_japan_l10n_pck.file_seq_directory_w')::varchar(15), cd_establishment_p, nm_usuario_p, current_setting('carestream_ris_japan_l10n_pck.file_sequence_w')::smallint, current_setting('carestream_ris_japan_l10n_pck.interface_event_w')::integer);
            select carestream_japan_l10n_pck.get_file_name(current_setting('carestream_ris_japan_l10n_pck.cd_integartion_event_w')::philips_json.get['control_code'].value_of(), current_setting('carestream_ris_japan_l10n_pck.radiology_dept_code_w')::varchar(2), current_setting('carestream_ris_japan_l10n_pck.his_system_code')::varchar(2), current_setting('carestream_ris_japan_l10n_pck.file_sequence_w')::smallint)
            into STRICT current_setting('carestream_ris_japan_l10n_pck.data_file_name_w')::varchar(100)
;

        end if;

        PERFORM set_config('carestream_ris_japan_l10n_pck.ds_script_w', 'carestream_ris_japan_l10n_pck.patient_discharge_info ('
                       || unique_key_p
                       || ','''
                       || message_type_p
                       || ''', '''
                       || processing_type_p
                       || ''', '''
                       || ':nm_exec_user' 
                       || ''' ,'
                       || ':nr_seq_int_call_log'
                       || ', '''
                       || current_setting('carestream_ris_japan_l10n_pck.data_file_name_w')::varchar(100)
                       || ''' ,'
                       || cd_establishment_p
                       || ', ''S'');', false);

        CALL CALL carestream_ris_japan_l10n_pck.perform_integration(unique_key_p, message_type_p, nm_usuario_p, current_setting('carestream_ris_japan_l10n_pck.log_sequence_number_w')::integration_message_log.nr_seq_int_call_log%type, current_setting('carestream_ris_japan_l10n_pck.data_file_name_w')::varchar(100),
                            current_setting('carestream_ris_japan_l10n_pck.log_file_name_w')::varchar(100), cd_establishment_p, current_setting('carestream_ris_japan_l10n_pck.file_seq_directory_w')::varchar(15), current_setting('carestream_ris_japan_l10n_pck.cd_integartion_event_w')::philips_json, current_setting('carestream_ris_japan_l10n_pck.ds_message_w')::varchar(255),
                            current_setting('carestream_ris_japan_l10n_pck.ds_script_w')::integration_call_log.ds_resend_script%type, current_setting('carestream_ris_japan_l10n_pck.nm_interface_w')::varchar(255), current_setting('carestream_ris_japan_l10n_pck.interface_event_w')::integer, current_setting('carestream_ris_japan_l10n_pck.patient_mrn_w')::varchar(10), ie_retransmit_p);

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carestream_ris_japan_l10n_pck.patient_discharge_info ( unique_key_p bigint, message_type_p text, processing_type_p text, nm_usuario_p text, log_sequence_number_p text default 0, file_name_p text default null, cd_establishment_p bigint default 1, ie_retransmit_p text  DEFAULT NULL) FROM PUBLIC;
