SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION clinical_notes_pck.get_clinical_notes_permission ( nr_sequencia_p bigint, nr_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE

    ie_access_w            varchar(10) := 'S';
    cd_setor_atendimento_w bigint;
    cd_perfil_w            bigint;
    qt_reg_w               bigint;

BEGIN
    cd_perfil_w            := coalesce(obter_perfil_ativo,0);
    cd_setor_atendimento_w := coalesce(obter_setor_atendimento(nr_atendimento_p),0);
    select count(1)
    into STRICT qt_reg_w
    from clinical_notes_rule
    where nr_sequencia = nr_sequencia_p;
    if (qt_reg_w       > 0) then
      select count(1)
      into STRICT qt_reg_w
      from clinical_notes_rule
      where nr_sequencia                                   = nr_sequencia_p
      and coalesce(cd_perfil,cd_perfil_w)                       = cd_perfil_w
      and coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
      and cd_estabelecimento                               = wheb_usuario_pck.get_cd_estabelecimento;
      if (qt_reg_w                                         = 0) then
        ie_access_w                                       := 'N';
      end if;
    end if;
    return ie_access_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
