CREATE OR REPLACE FUNCTION clinical_notes_pck.get_no_time_interval (cd_interval_p text) RETURNS varchar AS $body$
DECLARE

is_no_time_interval_w varchar(1):= 'N';
ds_return_w varchar(1) := 'N';

BEGIN
select coalesce(ie_hide_schedules,'N')
into STRICT is_no_time_interval_w 
