        end if;
        if (r_c01.ie_field   = 'ENTRY_DATE' and (ds_entry_date_w IS NOT NULL AND ds_entry_date_w::text <> ''))then
          if (coalesce(ds_content_w::text, '') = '' ) then
            ds_content_w   := '<b>' || obter_desc_expressao(722008) || '</b>';
