        if (r_c01.ie_field   = 'NURSE_PRIMARY' and (nm_nurse_primary_w IS NOT NULL AND nm_nurse_primary_w::text <> ''))then
          if (coalesce(ds_content_w::text, '') = '' ) then
            ds_content_w   := '<b>' || obter_desc_expressao(722008) || '</b>';
