SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION clinical_packway_preview_pck.get_preview_patient_outcome (nr_seq_etapa_p bigint, ie_count_p text DEFAULT 'N', ie_nome_amigavel_p text DEFAULT 'N') RETURNS varchar AS $body$
DECLARE


        c1 CURSOR FOR
            SELECT coalesce( CASE WHEN ie_nome_amigavel_p='S' THEN  c.ds_resultado_paciente  ELSE c.ds_resultado END , c.ds_resultado ) DS_RESULTADO
              FROM
                   protocolo_int_pac_etapa  b,
                   protocolo_int_pac_resul  c
            WHERE  b.nr_sequencia = c.nr_seq_etapa
              AND b.nr_sequencia = nr_seq_etapa_p
              AND c.ie_situacao = 'A'
              AND coalesce(b.ie_evento_sumario,'N') = 'N'
            ORDER BY c.nr_ordem_apresentacao;

        outcomes_w varchar(2000) := '';


BEGIN

        FOR outcome IN c1 LOOP
            outcomes_w := outcomes_w || ' ' || outcome.ds_resultado || ',';
        END LOOP;
            outcomes_w := substr(outcomes_w, 0, Length(outcomes_w) - 1);
        RETURN outcomes_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
