SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION clinical_panorama_md_pck.get_icon_result_exam ( ds_flag_p text, ie_tipo_valor_p bigint, nr_min_exibir_result_p bigint) RETURNS varchar AS $body$
DECLARE

icon_result_exam_w            varchar(255);

BEGIN
	icon_result_exam_w := 'assets/icons/status-and-feedback/microscope-check.svg';
	
	IF ds_flag_p in ('LL','HH') OR ie_tipo_valor_p = 2 THEN
		icon_result_exam_w := 'assets/icons/status-and-feedback/microscope-major-warning.svg';
	END IF;
	
	IF (ds_flag_p in ('L','H') OR ie_tipo_valor_p = 1) AND nr_min_exibir_result_p <> 0 THEN
		icon_result_exam_w := 'assets/icons/status-and-feedback/microscope-minor-warning.svg';
	END IF;
	
	return icon_result_exam_w;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
